mapper.select:
SELECT * FROM( WITH RECURSIVE ClaimHierarchy AS ( SELECT gclaim_no, goods_no, mem_id, content, regidate, updatedate, status, 0 AS answer_no, 1 AS level FROM gclaim
goods_no = #{goods_no}
UNION ALL
SELECT ac.gclaim_no, gc.goods_no, gc.mem_id, ac.content, ac.regidate, ac.updatedate, ac.status, ac.answer_no, ch.level + 1 AS level FROM answer_claim ac JOIN ClaimHierarchy ch ON ac.gclaim_no = ch.gclaim_no JOIN gclaim gc ON ac.gclaim_no = gc.gclaim_no WHERE LEVEL = 1 ) SELECT LEVEL, gclaim_no, goods_no, mem_id, content, regidate, updatedate, status, answer_no, ROW_NUMBER() over(ORDER BY gclaim_no, answer_no) AS rownum, count(1) over() AS total FROM ClaimHierarchy ORDER BY gclaim_no, answer_no )b WHERE rownum BETWEEN (#{section}-1)*100+(#{pageNum}-1)*10+1 AND (#{section}-1)*100+#{pageNum}*10;

mapper.update:
update gclaim
set
content=#{content},
status=#{status}
where gclaim_no=#{glno}

update answer_claim
set
title=#{title},
content=#{content},
status=#{status}
where answer_no=#{anscno}
