mapper.select:
select item_no, item_name, img_name, amount, price, category from ( select item_no, item_name, img_name, amount, price, category, ROW_NUMBER() over(ORDER BY regdate desc) AS rownum from item i
AND category LIKE #{category}
AND item_name LIKE CONCAT ('%',#{keyword},'%')
) i WHERE
rownum > (#{pageNum}-1) * #{amount} and rownum <= #{amount} * #{pageNum}
order by rownum

		select count(*) from item



                AND category LIKE #{category}



                AND item_name LIKE CONCAT ('%',#{keyword},'%')
