mapper.select:
select *	
from (
	select
		g.pname as title,

	from review r 
	inner join goods g on(g.goods_no = r.goods_no)
	where r.goods_no = #{no}
) t
where rownum BETWEEN (#{section}-1)*10*100+(#{pageNum}-1)*10+1 AND (#{section}-1)*100+#{pageNum}*10
order by rownum

select 

from review r
where review_no = #{reviewNo}

mapper.update:
update review
set titl e= #{title},
	content = #{content},
	score = #{score}
where review_no = #{reviewNo}
